(1, 1, 'فایل سکلتور اختصاصی', 'پسوند دلخواهتون رو توی یه String[] قرار بدید به این صورت : new String[]{\".jpg\"} فولدر های شامل \".nomedia\" ایندکس نخواهند شد! توضیحات : https://answers.uncox.com/android/question/31642 گیت هاب : https://github.com/criss721/Android-FileSelector', 'import android.app.Activity;\r\nimport android.app.Dialog;\r\nimport android.graphics.Typeface;\r\nimport android.os.Environment;\r\nimport android.support.v7.widget.LinearLayoutManager;\r\nimport android.support.v7.widget.RecyclerView;\r\nimport android.util.Log;\r\nimport android.util.TypedValue;\r\nimport android.view.Gravity;\r\nimport android.view.View;\r\nimport android.view.ViewGroup;\r\nimport android.view.Window;\r\nimport android.widget.LinearLayout;\r\nimport android.widget.TextView;\r\n\r\nimport java.io.File;\r\nimport java.util.ArrayList;\r\n\r\n\r\n\r\n/**\r\n * Created by Javad on 2019-11-28 at 4:43 PM.\r\n */\r\n\r\npublic class FileSelector {\r\n\r\n private Activity context;\r\n private String[] extensions;\r\n private ArrayList<SelectedFile> itemsData = new ArrayList<>();\r\n public static final String MP4 = \".mp4\", MP3 = \".mp3\", JPG = \".jpg\", JPEG = \".jpeg\", PNG = \".png\", DOC = \".doc\", DOCX = \".docx\", XLS = \".xls\", XLSX = \".xlsx\", PDF = \".pdf\";\r\n\r\n\r\n public FileSelector(Activity context, String[] extensions) {\r\n this.context = context;\r\n this.extensions = extensions;\r\n }\r\n\r\n public interface OnSelectListener {\r\n void onSelect(String path);\r\n\r\n }\r\n\r\n public void selectFile(OnSelectListener listener) {\r\n String sdCard = Environment.getExternalStorageDirectory().getAbsolutePath();\r\n listOfFile(new File(sdCard));\r\n dialogFileList(listener);\r\n }\r\n\r\n private void listOfFile(File dir) {\r\n File[] list = dir.listFiles();\r\n\r\n for (File file : list) {\r\n if (file.isDirectory()) {\r\n if (!new File(file, \".nomedia\").exists() && !file.getName().startsWith(\".\")) {\r\n Log.w(\"LOG\", \"IS DIR \" + file);\r\n listOfFile(file);\r\n }\r\n } else {\r\n String path = file.getAbsolutePath();\r\n\r\n for (String ext : extensions) {\r\n if (path.endsWith(ext)) {\r\n SelectedFile selectedFile = new SelectedFile();\r\n\r\n selectedFile.path = path;\r\n String[] split = path.split(\"/\");\r\n selectedFile.name = split[split.length - 1];\r\n itemsData.add(selectedFile);\r\n Log.i(\"LOG\", \"ADD \" + selectedFile.path + \" \" + selectedFile.name);\r\n }\r\n }\r\n }\r\n }\r\n Log.d(\"LOG\", itemsData.size() + \" DONE\");\r\n }\r\n\r\n private void dialogFileList(OnSelectListener listener) {\r\n LinearLayout lytMain = new LinearLayout(context);\r\n lytMain.setLayoutParams(new LinearLayout.LayoutParams(ViewGroup.LayoutParams.WRAP_CONTENT, ViewGroup.LayoutParams.WRAP_CONTENT));\r\n lytMain.setOrientation(LinearLayout.VERTICAL);\r\n int p = convertToPixels(12);\r\n lytMain.setPadding(p, p, p, p);\r\n lytMain.setGravity(Gravity.CENTER);\r\n\r\n TextView textView = new TextView(context);\r\n textView.setLayoutParams(new LinearLayout.LayoutParams(screenWidth(), ViewGroup.LayoutParams.WRAP_CONTENT));\r\n textView.setText(\"JDM File Selecor\");\r\n\r\n RecyclerView recyclerView = new RecyclerView(context);\r\n\r\n lytMain.addView(textView);\r\n lytMain.addView(recyclerView);\r\n\r\n final Dialog dialog = new Dialog(context);\r\n dialog.requestWindowFeature(Window.FEATURE_NO_TITLE);\r\n // dialog.getWindow().setBackgroundDrawable(new ColorDrawable(android.graphics.Color.TRANSPARENT));\r\n dialog.setContentView(lytMain);\r\n dialog.setCancelable(true);\r\n dialog.show();\r\n\r\n AdapterFile adapter = new AdapterFile(dialog, listener, itemsData);\r\n recyclerView.setAdapter(adapter);\r\n LinearLayoutManager LayoutManager = new LinearLayoutManager(context);\r\n LayoutManager.setOrientation(LinearLayoutManager.VERTICAL);\r\n recyclerView.setLayoutManager(LayoutManager);\r\n }\r\n\r\n private int convertToPixels(int dp) {\r\n float scale = context.getResources().getDisplayMetrics().density;\r\n return (int) (dp * scale + 0.5f);\r\n }\r\n\r\n private int screenWidth() {\r\n return context.getResources().getDisplayMetrics().widthPixels;\r\n }\r\n\r\n private class SelectedFile {\r\n public String path = \"\";\r\n public String name = \"\";\r\n }\r\n\r\n private class AdapterFile extends RecyclerView.Adapter<AdapterFile.ViewHolder> {\r\n\r\n private ArrayList<SelectedFile> itemsData;\r\n private OnSelectListener listener;\r\n private Dialog dialog;\r\n\r\n public AdapterFile(Dialog dialog, OnSelectListener listener, ArrayList<SelectedFile> itemsData) {\r\n this.itemsData = itemsData;\r\n this.listener = listener;\r\n this.dialog = dialog;\r\n }\r\n\r\n // Create new views (invoked by the layout manager)\r\n @Override\r\n public ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {\r\n\r\n LinearLayout linearLayout = new LinearLayout(context);\r\n linearLayout.setOrientation(LinearLayout.VERTICAL);\r\n TextView txtName = new TextView(context);\r\n TextView txtPath = new TextView(context);\r\n txtPath.setTypeface(txtPath.getTypeface(), Typeface.ITALIC);\r\n txtPath.setTextSize(TypedValue.COMPLEX_UNIT_SP, 11);\r\n\r\n linearLayout.addView(txtName);\r\n linearLayout.addView(txtPath);\r\n\r\n ViewHolder viewHolder = new ViewHolder(linearLayout);\r\n return viewHolder;\r\n }\r\n\r\n // inner class to hold a reference to each item of RecyclerView\r\n public class ViewHolder extends RecyclerView.ViewHolder {\r\n\r\n public LinearLayout linearLayout;\r\n public TextView txtName;\r\n public TextView txtPath;\r\n\r\n public ViewHolder(View itemLayoutView) {\r\n super(itemLayoutView);\r\n\r\n linearLayout = (LinearLayout) itemLayoutView;\r\n txtName = (TextView) linearLayout.getChildAt(0);\r\n txtPath = (TextView) linearLayout.getChildAt(1);\r\n }\r\n }\r\n\r\n\r\n @Override\r\n public int getItemCount() {\r\n return itemsData.size();\r\n }\r\n\r\n @Override\r\n public void onBindViewHolder(ViewHolder viewHolder, final int position) {\r\n final SelectedFile selectedFile = itemsData.get(position);\r\n\r\n viewHolder.txtName.setText(selectedFile.name);\r\n viewHolder.txtPath.setText(selectedFile.path);\r\n\r\n viewHolder.linearLayout.setOnClickListener(new View.OnClickListener() {\r\n @Override\r\n public void onClick(View v) {\r\n dialog.dismiss();\r\n listener.onSelect(selectedFile.path);\r\n }\r\n });\r\n }\r\n }\r\n}', '2020-07-24 05:17:16', 24),
(2, 2, 'اگر آموزش های توسعه ی وب با ساختار lamp را از سایت uncox خوب مطالعه نکرده باشید . درک کردن این قابلیت گنگ است. برای استفاده باید حتماً توضیحات را با دقت بخوانید و سپس استفاده کنید.', 'این قابلیت برروی پروژه ی notes که داخل آموزش توسعه ی وب با ساختار lamp همین سایت هست نوشته شده. همچنین برای بهبود یافتن و کامل شدن باید خودتان پی گیر باشید. اما برای سایت های ساده به خوبی کار میکند.\r\n\r\nدر ابتدا باید فایل sql را که دارای table ی به نام x_hashlink که برای همین قابلیت لازم است را به دیتابیس سایت خود import کنید که در پیوست همین اسنیپ قرار دارد.\r\n\r\nتوجه داشته باشید که این table باید ساختار مشخصی داشته باشد بنابراین اضافه یا حذف هر یک از فیلد های باعث خرابکاری میشود و حتما برای فیلد های اضافه تر باید برنامه نویسی بیشتری صورت گیرد.\r\n\r\nدر مرحله ی بعد باید متد های فایل UserModel در داخل پوشه model وارد کنید. که هر کدام یک کاری را درسمت دیتا بیس انجام می دهند. پس هرگونه تغییر به عهده ی خودتان است و حتماً اگر به سر فصل 7 که قسمت security هست رسیدید حتماً این فایل را طبق قاعده ی امن شده ی خود بنویسید. این که این متد ها چطور کار میکنند باید به فایل userController نگاه کنید تا متوجه شوید.\r\n\r\nدر مرحله ی سوم فایل UserController را به usercontroller خود اضافه کنید . متد forgotPassword ابتدا سعی در دریافت ایمیل از کاربر را دارد و در صورتی که نتوانست ایمیل را درافت کند فرمی که کاربر لازم است آنرا پر کند را نمایش می دهد. این اطلاعات یک ایمیل از کاربر میخواهد که باید وجود داشته باشد و سپس به دنبال آن در دیتابیس می گردد با متد fetch_by_email و اگر این کاربر وجود نداشته باشد پیغام مناسب با سیستم message نمایش داده میشود و در غیر این صورت با استفاده از ایمیل اطلاعات مربوطه را دریافت می کند و نام کاربری ، کاربر را به صورت encrypt شده و همرا با ایمیل به متد send_hashed_email فرستاده میشود و ایمیل را تحویل این متد private می دهد و سپس این متد در دیتابیس hashlink ایمیل کاربر را درج میکند و سپس آنرا همراه با نام کاربری encrypt شده به فایل مورد نیاز فرستاده میشود و توجه کنید کاربر در اینجا نمی تواند ایمیل خود را ببیند اما میتواند هم ببیند در هر صورت باید دوباره بازگردانده شود چون لازم است. در صفحه ی مربوطه از کاربر خواسته شده که نام کاربری خود را جهت صحت کامل کند. فایل مربوطه پاسخ کاربر را به متد get_name_from_user میفرستد که این متد نیز برای اینکه از صحت ایمیل مطمئن شود باز هم اطلاعات را دریافت میکند و در صورت موفقیت به مرحله ی بعدی می رود . شاید بگویید قبلاً ایمیل بازرسی شده چرا دوباره باید بازرسی شود ، توجه کنید که اگر کاربر کمی از برنامه نویسی سر در بیاورد ممکن است در صفحه ای که دارد نام کاربری خود را وارد میکند ممکن است ایمیل خود را تغییر دهد به همین خاطر دوباره ایمیل بازرسی میشود.', ' public function forgotPassword() {\r\n    if(isset($_POST[\'email\'])) {\r\n      $email = $_POST[\'email\'];\r\n      $record = UserModel::fetch_by_email($email);\r\n      if($record == null) {\r\n        message(\'fail\', \'چنین کاربری وجود ندارد لظفاً مجدداً تلاش کنید.\', true);\r\n      }\r\n      $letter = $record[\'nickname\'];\r\n      $record = UserModel::fetch_by_email($email);\r\n      if($email == $record[\'email\']) {\r\n        $this->send_hashed_email($email, hashedString($letter, 2));\r\n      }\r\n    } else {\r\n      View::render(\'/user/link-forgot-password.php\');\r\n    }\r\n  }\r\n  \r\n  private function send_hashed_email($email, $hashedName){\r\n    UserModel::insert_forgot_email($email);\r\n    $data[\'hashedName\'] = $hashedName;\r\n    $data[\'email\'] = $email;\r\n    View::render(\'/user/get-nickname.php\', $data);\r\n  }\r\n\r\n  public function get_name_from_user() {\r\n    if(!isset($_POST[\'answer\'])) {\r\n      message(\'fail\', \'لطفاً نام کاربری خود را وارد کنید.<br><br> <a href=\"\' . baseUrl() . \'/forgotPassword\">بازگشت به صفحه ی قبلی </a>\', true);\r\n    }\r\n    $email = $_POST[\'email\'];\r\n    $record = UserModel::fetch_by_email($email);\r\n    if(!($record[\'nickname\'] == $_POST[\'answer\'])){\r\n      message(\'fail\', \'لطفاً نام کاربری خود را به درستی وارد کنید.<br><br> <a href=\"\' . baseUrl() . \'/forgotPassword\">بازگشت به صفحه ی قبلی </a>\', true);\r\n    }\r\n\r\n    if($record != null) {\r\n      $hash = generateHash(28);\r\n      UserModel::insert_forgot_hash($hash, $record[\'email\']);\r\n      $this->send_hashed_link_for_email($record[\'email\'], $hash);\r\n      message(\'success\', \'ایمیل لینک رمز جدید به ایمیل شما ارسال شد.\', true);\r\n    } else {\r\n      message(\'fail\', \'لطفاً نام کاربری خود را به درستی وارد کنید.<br><br> <a href=\"\' . baseUrl() . \'/forgotPassword\">بازگشت به صفحه ی قبلی </a>\', true);\r\n    }\r\n  }\r\n\r\n  public function getNewPassword($hash = null) {\r\n    if(!isset($_POST[\'password1\']) || !isset($_POST[\'password2\'])) {\r\n      $data[\'hash\'] = $hash;\r\n      View::render(\'/user/get-new-password.php\', $data);\r\n      return;\r\n    }\r\n\r\n    $hash = $_POST[\'hash\'];\r\n    $record = UserModel::fetch_by_hash($hash);\r\n\r\n    if($record == null) {\r\n      message(\'fail\', \'این لینک اشتباه است .\', true);\r\n    }\r\n\r\n    $time = getCurrentDateTime();\r\n    if($time > $record[\'endDate\']) {\r\n      message(\'fail\', \'درخواست رمز جدید شما منقضی شده ، لطفاً دوباره درخواست دهید. <br><br> <a href=\"\' . baseUrl() . \'/login\">لینک صفحه ی ورود</a>\', true);\r\n    }\r\n\r\n    $password1 = $_POST[\'password1\'];\r\n    $password2 = $_POST[\'password2\'];\r\n\r\n    if($password1 == null || $password2 == null){\r\n      message(\'fail\', _password_empty, true);\r\n    }\r\n    if($password1 != $password2) {\r\n      message(\'fail\', _passwords_not_matched, true);\r\n    }\r\n\r\n    UserModel::update_password($record[\'email\'], $password1);\r\n    UserModel::remove_hashlink($record[\'email\']);\r\n    message(\'success\', \'رمز عبور شما با موفقیت تغییر کرد. <br><br> <a href=\"\' . baseUrl() . \'/login\">لینک صفحه ی ورود</a>\', true);\r\n  }\r\n\r\n  private function send_hashed_link_for_email($email, $hash) {\r\n    mail($email, \'فراموشی رمز عبور در سایت یادداشت ها\', \'لطفاً برای وارد کردن رمز جدید روی لینک زیر کلیک کنید. \' . \'project.libcode.ir/notes-v3/user/getNewPassword/\' . $hash . \'\');\r\n  }', '2020-07-22 07:30:18', 98),
(3, 2, 'دوستان با این کلاس میتونید عکس رو با به وسیله انگشت scale in و scale out کنید', 'دوستان یه کلاس بسازین و کد های پایین رو داخلش کپی کنین.بعد برای استفاده هم مثل تعریف imageView ازش استفاده کنید\r\n\r\n', '\r\nimport android.content.Context;\r\nimport android.graphics.Matrix;\r\nimport android.graphics.PointF;\r\nimport android.graphics.drawable.Drawable;\r\nimport android.util.AttributeSet;\r\nimport android.view.MotionEvent;\r\nimport android.view.ScaleGestureDetector;\r\nimport android.widget.ImageView;\r\n\r\npublic class TouchImageView extends ImageView {\r\n\r\n// region . Static fields .\r\n\r\n    static final int NONE = 0;\r\n    static final int DRAG = 1;\r\n    static final int ZOOM = 2;\r\n    static final int CLICK = 3;\r\n\r\n// endregion . Static fields .\r\n\r\n// region . Fields .\r\n\r\n    private int mode = NONE;\r\n\r\n    private Matrix mMatrix = new Matrix();\r\n\r\n    private PointF mLastTouch = new PointF();\r\n    private PointF mStartTouch = new PointF();\r\n    private float minScale = 0.5f;\r\n    private float maxScale = 4f;\r\n    private float[] mCriticPoints;\r\n\r\n    private float mScale = 1f;\r\n    private float mRight;\r\n    private float mBottom;\r\n    private float mOriginalBitmapWidth;\r\n    private float mOriginalBitmapHeight;\r\n\r\n    private ScaleGestureDetector mScaleDetector;\r\n\r\n//endregion . Fields .\r\n\r\n    // region . Ctor .\r\n    public TouchImageView(Context context) {\r\n        super(context);\r\n        init(context);\r\n    }\r\n\r\n    public TouchImageView(Context context, AttributeSet attrs) {\r\n        super(context, attrs);\r\n        init(context);\r\n    }\r\n\r\n    public TouchImageView(Context context, AttributeSet attrs, int defStyleAttr) {\r\n        super(context, attrs, defStyleAttr);\r\n        init(context);\r\n    }\r\n\r\n// endregion . Ctor .\r\n\r\n// region . Overrider .\r\n\r\n    @Override\r\n    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {\r\n        super.onMeasure(widthMeasureSpec, heightMeasureSpec);\r\n        int bmHeight = getBmHeight();\r\n        int bmWidth = getBmWidth();\r\n\r\n        float width = getMeasuredWidth();\r\n        float height = getMeasuredHeight();\r\n        float scale = 1;\r\n\r\n        // If image is bigger then display fit it to screen.\r\n        if (width < bmWidth || height < bmHeight) {\r\n            scale = width > height ? height / bmHeight : width / bmWidth;\r\n        }\r\n\r\n        mMatrix.setScale(scale, scale);\r\n        mScale = 1f;\r\n\r\n        mOriginalBitmapWidth = scale * bmWidth;\r\n        mOriginalBitmapHeight = scale * bmHeight;\r\n\r\n        // Center the image\r\n        float redundantYSpace = (height - mOriginalBitmapHeight);\r\n        float redundantXSpace = (width - mOriginalBitmapWidth);\r\n\r\n        mMatrix.postTranslate(redundantXSpace / 2, redundantYSpace / 2);\r\n\r\n        setImageMatrix(mMatrix);\r\n    }\r\n\r\n    @Override\r\n    public boolean onTouchEvent(MotionEvent event) {\r\n        mScaleDetector.onTouchEvent(event);\r\n\r\n        mMatrix.getValues(mCriticPoints);\r\n        float translateX = mCriticPoints[Matrix.MTRANS_X];\r\n        float trnslateY = mCriticPoints[Matrix.MTRANS_Y];\r\n        PointF currentPoint = new PointF(event.getX(), event.getY());\r\n\r\n        switch (event.getAction()) {\r\n            //when one finger is touching\r\n            //set the mode to DRAG\r\n            case MotionEvent.ACTION_DOWN:\r\n                mLastTouch.set(event.getX(), event.getY());\r\n                mStartTouch.set(mLastTouch);\r\n                mode = DRAG;\r\n                break;\r\n            //when two fingers are touching\r\n            //set the mode to ZOOM\r\n            case MotionEvent.ACTION_POINTER_DOWN:\r\n                mLastTouch.set(event.getX(), event.getY());\r\n                mStartTouch.set(mLastTouch);\r\n                mode = ZOOM;\r\n                break;\r\n            //when a finger moves\r\n            //If mode is applicable move image\r\n            case MotionEvent.ACTION_MOVE:\r\n\r\n                //if the mode is ZOOM or\r\n                //if the mode is DRAG and already zoomed\r\n                if (mode == ZOOM || (mode == DRAG && mScale > minScale)) {\r\n\r\n                    // region . Move  image.\r\n\r\n                    float deltaX = currentPoint.x - mLastTouch.x;// x difference\r\n                    float deltaY = currentPoint.y - mLastTouch.y;// y difference\r\n                    float scaleWidth = Math.round(mOriginalBitmapWidth * mScale);// width after applying current scale\r\n                    float scaleHeight = Math.round(mOriginalBitmapHeight * mScale);// height after applying current scale\r\n\r\n                    // Move image to lef or right if its width is bigger than display width\r\n                    if (scaleWidth > getWidth()) {\r\n                        if (translateX + deltaX > 0) {\r\n                            deltaX = -translateX;\r\n                        } else if (translateX + deltaX < -mRight) {\r\n                            deltaX = -(translateX + mRight);\r\n                        }\r\n                    } else {\r\n                        deltaX = 0;\r\n                    }\r\n                    // Move image to up or bottom if its height is bigger than display height\r\n                    if (scaleHeight > getHeight()) {\r\n                        if (trnslateY + deltaY > 0) {\r\n                            deltaY = -trnslateY;\r\n                        } else if (trnslateY + deltaY < -mBottom) {\r\n                            deltaY = -(trnslateY + mBottom);\r\n                        }\r\n                    } else {\r\n                        deltaY = 0;\r\n                    }\r\n\r\n                    //move the image with the matrix\r\n                    mMatrix.postTranslate(deltaX, deltaY);\r\n                    //set the last touch location to the current\r\n                    mLastTouch.set(currentPoint.x, currentPoint.y);\r\n\r\n                    // endregion . Move image .\r\n                }\r\n                break;\r\n            //first finger is lifted\r\n            case MotionEvent.ACTION_UP:\r\n                mode = NONE;\r\n                int xDiff = (int) Math.abs(currentPoint.x - mStartTouch.x);\r\n                int yDiff = (int) Math.abs(currentPoint.y - mStartTouch.y);\r\n                if (xDiff < CLICK && yDiff < CLICK)\r\n                    performClick();\r\n                break;\r\n            // second finger is lifted\r\n            case MotionEvent.ACTION_POINTER_UP:\r\n                mode = NONE;\r\n                break;\r\n        }\r\n        setImageMatrix(mMatrix);\r\n        invalidate();\r\n        return true;\r\n    }\r\n\r\n//endregion . Overrides .\r\n\r\n// region . Privates .\r\n\r\n    private void init(Context context) {\r\n        super.setClickable(true);\r\n        mScaleDetector = new ScaleGestureDetector(context, new ScaleListener());\r\n        mCriticPoints = new float[9];\r\n        setImageMatrix(mMatrix);\r\n        setScaleType(ScaleType.MATRIX);\r\n    }\r\n\r\n    private int getBmWidth() {\r\n        Drawable drawable = getDrawable();\r\n        if (drawable != null) {\r\n            return drawable.getIntrinsicWidth();\r\n        }\r\n        return 0;\r\n    }\r\n\r\n    private int getBmHeight() {\r\n        Drawable drawable = getDrawable();\r\n        if (drawable != null) {\r\n            return drawable.getIntrinsicHeight();\r\n        }\r\n        return 0;\r\n    }\r\n\r\n//endregion . Privates .\r\n\r\n// region . Internal classes .\r\n\r\n    private class ScaleListener extends ScaleGestureDetector.SimpleOnScaleGestureListener {\r\n\r\n        @Override\r\n        public boolean onScaleBegin(ScaleGestureDetector detector) {\r\n            mode = ZOOM;\r\n            return true;\r\n        }\r\n\r\n        @Override\r\n        public boolean onScale(ScaleGestureDetector detector) {\r\n            float scaleFactor = detector.getScaleFactor();\r\n            float newScale = mScale * scaleFactor;\r\n            if (newScale < maxScale && newScale > minScale) {\r\n                mScale = newScale;\r\n                float width = getWidth();\r\n                float height = getHeight();\r\n                mRight = (mOriginalBitmapWidth * mScale) - width;\r\n                mBottom = (mOriginalBitmapHeight * mScale) - height;\r\n\r\n                float scaledBitmapWidth = mOriginalBitmapWidth * mScale;\r\n                float scaledBitmapHeight = mOriginalBitmapHeight * mScale;\r\n\r\n                if (scaledBitmapWidth <= width || scaledBitmapHeight <= height) {\r\n                    mMatrix.postScale(scaleFactor, scaleFactor, width / 2, height / 2);\r\n                } else {\r\n                    mMatrix.postScale(scaleFactor, scaleFactor, detector.getFocusX(), detector.getFocusY());\r\n                }\r\n            }\r\n            return true;\r\n        }\r\n    }\r\n\r\n// endregion . Internal classes .\r\n}\r\n\r\n\r\n', '2020-07-14 09:30:18', 66),
